import React from 'react';
import { StyleSheet, Text, View, TextInput, Button, Alert, ScrollView, TouchableOpacity } from 'react-native';

export default function App() {
  const [password, setPassword] = React.useState('');
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [products, setProducts] = React.useState([]);
  const [editingProduct, setEditingProduct] = React.useState(null);
  const [sales, setSales] = React.useState([]);

  const PASSWORD = 'admin123';

  // Login handler
  const handleLogin = () => {
    if (password === PASSWORD) {
      setLoggedIn(true);
      setPassword('');
      fetchProducts();
      fetchSales();
    } else {
      Alert.alert('Erro', 'Senha incorreta. Tente novamente.');
    }
  };

  // Fetch products from API
  const fetchProducts = async () => {
    try {
      const response = await fetch('https://joaozinho-celular.onrender.com/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      Alert.alert('Erro', 'Falha ao buscar produtos.');
    }
  };

  // Fetch sales from API
  const fetchSales = async () => {
    try {
      const response = await fetch('https://joaozinho-celular.onrender.com/sales');
      const data = await response.json();
      setSales(data);
    } catch (error) {
      Alert.alert('Erro', 'Falha ao buscar vendas.');
    }
  };

  // Render login screen
  if (!loggedIn) {
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Login - Joãozinho Celular</Text>
        <TextInput
          style={styles.input}
          placeholder="Digite a senha"
          secureTextEntry
          value={password}
          onChangeText={setPassword}
        />
        <Button title="Entrar" onPress={handleLogin} />
      </View>
    );
  }

  // Render main app screen
  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Produtos</Text>
      {products.length === 0 ? (
        <Text>Nenhum produto cadastrado.</Text>
      ) : (
        products.map((product) => (
          <View key={product.id} style={styles.product}>
            <Text>{product.name}</Text>
            <Text>Preço Varejo: R$ {parseFloat(product.retail_price).toFixed(2)}</Text>
            <Text>Preço Atacado: R$ {parseFloat(product.wholesale_price).toFixed(2)}</Text>
            <Text>Quantidade: {product.quantity}</Text>
          </View>
        ))
      )}

      <Text style={styles.title}>Vendas</Text>
      {sales.length === 0 ? (
        <Text>Nenhuma venda registrada.</Text>
      ) : (
        sales.map((sale) => (
          <View key={sale.id} style={styles.sale}>
            <Text>Produto: {sale.product}</Text>
            <Text>Quantidade: {sale.quantity}</Text>
            <Text>Preço Total: R$ {parseFloat(sale.total_price).toFixed(2)}</Text>
            <Text>Data: {sale.date}</Text>
            <Text>Hora: {sale.time}</Text>
          </View>
        ))
      )}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#000000',
  },
  title: {
    fontSize: 24,
    fontWeight: '700',
    color: '#6a0dad',
    marginBottom: 10,
    textTransform: 'uppercase',
  },
  input: {
    backgroundColor: '#1e1e1e',
    color: '#ffffff',
    padding: 10,
    marginBottom: 10,
    borderRadius: 8,
    borderColor: '#6a0dad',
    borderWidth: 1,
  },
  product: {
    backgroundColor: '#121212',
    padding: 10,
    marginBottom: 10,
    borderRadius: 8,
  },
  sale: {
    backgroundColor: '#121212',
    padding: 10,
    marginBottom: 10,
    borderRadius: 8,
  },
});
