import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, TextInput, Button, Alert, ScrollView, TouchableOpacity, Switch } from 'react-native';

export default function SalesScreen({ onBack }) {
  const [products, setProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [quantity, setQuantity] = useState('');
  const [priceType, setPriceType] = useState('retail'); // 'retail' or 'wholesale'
  const [amountReceived, setAmountReceived] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const API_URL = 'http://localhost:3001';

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch(`${API_URL}/products`);
      if (!response.ok) throw new Error('Erro ao buscar produtos');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      Alert.alert('Erro', error.message);
    }
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getPrice = (product) => {
    return priceType === 'retail' ? product.retail_price : product.wholesale_price;
  };

  const handleSubmit = async () => {
    if (!selectedProductId) {
      Alert.alert('Erro', 'Por favor, selecione um produto.');
      return;
    }
    const qty = parseInt(quantity);
    if (isNaN(qty) || qty < 1) {
      Alert.alert('Erro', 'Por favor, insira uma quantidade válida.');
      return;
    }
    const price = getPrice(products.find(p => p.id === selectedProductId));
    const totalPrice = price * qty;
    const received = parseFloat(amountReceived);
    if (isNaN(received) || received < totalPrice) {
      Alert.alert('Erro', 'Valor recebido insuficiente.');
      return;
    }
    const change = received - totalPrice;

    try {
      const response = await fetch(`${API_URL}/sales`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          product_id: selectedProductId,
          quantity: qty,
          price_type: priceType,
          amount_received: received,
          change: change,
        }),
      });
      if (!response.ok) {
        const errorData = await response.json();
        Alert.alert('Erro', 'Erro ao registrar venda: ' + (errorData.error || response.statusText));
        return;
      }
      setSelectedProductId(null);
      setQuantity('');
      setAmountReceived('');
      Alert.alert('Sucesso', `Venda registrada com sucesso.\nTroco: R$ ${change.toFixed(2)}`);
      fetchProducts();
    } catch (error) {
      Alert.alert('Erro', 'Erro ao registrar venda: ' + error.message);
    }
  };

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Fazer Vendas</Text>

      <TextInput
        style={styles.searchInput}
        placeholder="Pesquisar produto"
        value={searchTerm}
        onChangeText={setSearchTerm}
      />

      <Text style={styles.label}>Selecione o Produto:</Text>
      {filteredProducts.length === 0 ? (
        <Text style={styles.emptyMessage}>Nenhum produto encontrado.</Text>
      ) : (
        filteredProducts.map((product) => (
          <TouchableOpacity
            key={product.id}
            style={[
              styles.productItem,
              selectedProductId === product.id && styles.selectedProduct,
            ]}
            onPress={() => setSelectedProductId(product.id)}
          >
            <Text style={styles.productText}>{product.name}</Text>
            <Text style={styles.productText}>
              Preço {priceType === 'retail' ? 'Varejo' : 'Atacado'}: R$ {getPrice(product).toFixed(2)}
            </Text>
            <Text style={styles.productText}>Quantidade: {product.quantity}</Text>
          </TouchableOpacity>
        ))
      )}

      <View style={styles.priceTypeContainer}>
        <Text style={styles.label}>Preço de Varejo</Text>
        <Switch
          value={priceType === 'wholesale'}
          onValueChange={(value) => setPriceType(value ? 'wholesale' : 'retail')}
        />
        <Text style={styles.label}>Preço de Atacado</Text>
      </View>

      <TextInput
        style={styles.input}
        placeholder="Quantidade"
        value={quantity}
        onChangeText={setQuantity}
        keyboardType="numeric"
      />

      <TextInput
        style={styles.input}
        placeholder="Valor Recebido"
        value={amountReceived}
        onChangeText={setAmountReceived}
        keyboardType="numeric"
      />

      {amountReceived !== '' && selectedProductId && quantity !== '' && (
        <View style={styles.changeContainer}>
          <Text style={styles.changeText}>
            Troco: R$ {(parseFloat(amountReceived) - getPrice(products.find(p => p.id === selectedProductId)) * parseInt(quantity)).toFixed(2)}
          </Text>
        </View>
      )}

      <Button title="Registrar Venda" onPress={handleSubmit} />
      <Button title="Voltar" onPress={onBack} />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: '700',
    color: '#6a0dad',
    marginBottom: 20,
    textTransform: 'uppercase',
    textAlign: 'center',
  },
  searchInput: {
    backgroundColor: '#1e1e1e',
    color: '#ffffff',
    padding: 10,
    marginBottom: 15,
    borderRadius: 8,
    borderColor: '#6a0dad',
    borderWidth: 1,
  },
  label: {
    color: '#dcdcdc',
    marginBottom: 10,
    textAlign: 'center',
  },
  productItem: {
    backgroundColor: '#121212',
    padding: 10,
    marginBottom: 10,
    borderRadius: 8,
  },
  selectedProduct: {
    borderColor: '#6a0dad',
    borderWidth: 2,
  },
  productText: {
    color: '#dcdcdc',
  },
  priceTypeContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 15,
  },
  input: {
    backgroundColor: '#1e1e1e',
    color: '#ffffff',
    padding: 10,
    marginBottom: 20,
    borderRadius: 8,
    borderColor: '#6a0dad',
    borderWidth: 1,
  },
  changeContainer: {
    backgroundColor: '#004d00',
    padding: 10,
    marginBottom: 20,
    borderRadius: 8,
  },
  changeText: {
    color: '#00ff00',
    fontWeight: '700',
    textAlign: 'center',
  },
  emptyMessage: {
    color: '#9a7ed1',
    fontStyle: 'italic',
    textAlign: 'center',
    marginTop: 20,
  },
});
